INSERT INTO `components_layout_text_images` VALUES (1,'About Us','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Atlantic Grease & Lubricants (AGL) is part of the Atlantic group of companies registered at Hamriyah Free Zone, Sharjah, UAE, with a rich history spanning over seven decades. We have been dedicated to continuous research and growth to achieve customer satisfaction and high quality standards.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"At Atlantic Grease and Lubricants, we are committed to constant development, with a strong\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"emphasis on making meaningful contributions to society, serving our valued customers, and\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"achieving sustainability goals. We have secured OEM approvals by major American and European car manufacturers.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"\"}]}]'),(2,'The Founder','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"The founder of Atlantic Grease and Lubricants embarked on this remarkable venture decades ago, laying the foundation for a company that would leave an indelible mark on the lubricants industry. He first established the company as a trading enterprise of oil and by-products in the 1940’s, followed by building the first manufacturing facility in 1987 Pakistan, Lahore.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Sheikh Naseem had a passion for innovation, and his dedication to deliver premium lubricant\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"solutions, which paved the way for Atlantic’s success. With extensive research and critical\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"understanding of lubricants and trends, he sought to create products that would not only meet but\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"exceed industry standards.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Sheikh Naseem’s vision and leadership qualities revolutionized Atlantic Grease and Lubricants\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"as a trusted name among customers worldwide. Today Atlantic withholds 6 manufacturing units\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"located across the globe, with headquarters residing in the United Arab Emirates. His Legacy\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"lives on in our operations, ensuring his vision remains at the core of everything we do at\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Atlantic\"}]}]'),(3,'Our Mission','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"At Atlantic Grease & Lubricants, our mission is to accelerate business transition with a sustainable future in mind. Our strategy, called Lubricating Towards the Future, is designed to deliver value to our customers, and the wider society as a whole. Our focus on sustainability is not just about fulfilling today’s lubricant needs for industries but also about finding renewable resources for future generations.\"}]}]'),(4,'Our Vision','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Our vision as a brand is to leverage business insights to provide valuable products that generate success for its customers and users. Moreover, we are committed to sustainability and reducing the environmental impacts of our production. With our dedication to performance, nonstop development, and sustainability, we view AGL as being the top company that comes to the mind of our customers when they need lubricating their machinery in any region of the world. So our vision is to provide a one-window solution to our customers according to their needs.\"}]}]');
