INSERT INTO `products` VALUES (4,'Atlantic Transfoformer Oil U','atlantic-transfoformer-oil-u','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC Transformer Oil U is a high-performance transformer oil. It is uninhibited (U) based on paraffinic mineral oil and free from PCB’s. For insulating and cooling of transformers and other oil-filled electrical equipment. It is a chemical stable formula which give high performance with extended life.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"APPLICATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC Transformer Oil U is widely recommended to be used in transformers, switches, electric generators in oil baths, etc. It is recommended to use new drums to install and maintain low technology in dry places. You must operate with low cleaning and free moisture concepts.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PRODUCT BENEFITS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Elevated rigid electric power that gives it excellent, comfortable properties.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Complete with the specifications of Norma NMX-J-123-ANCE, for safe mineral acids No Inhibitions type I.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Free of corrosive elements of azufre that should avoid the formation of rubber.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Free of Polychlorinated biphenyls components (PCB´s).\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"SPECIFICATIONS AND APPROVALS\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"IEC 60296\"}]}]','2024-08-15 06:34:18.749000','2025-05-26 11:50:29.239000','2024-08-15 06:36:06.754000',2,4,'208',NULL,NULL,NULL,'Atlantic Transfoformer Oil U'),(5,'ATLANTIC TRANSFORMER OIL TYPE II','atlantic-transformer-oil-type-ii-1','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC TRANSFORMER OIL ATLANTIC TRANSFORMER OIL TYPE II is developed through carefully selected raw materials and refining techniques. It has superior dielectric properties and a better heat transfer coefficient. It Surpass the current requirements of IEC 60296: 2012 standard specification\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"APPLICATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC TRANSFORMER OIL TYPE II is used in transformers and also used in many rubber compositions such as Styrene Butadiene Rubber for instance and the compatibility is excellent.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PRODUCT BENEFITS\",\"bold\":true}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Excellent performance and proven reliability.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"It serves as a medium to transfer heat away from the core, and helps maintain the water balance of the system.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"The naphthenic oil will reach a lower viscosity meaning more oil circulation in the transformer and better cooling.\"}]}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PRODUCT BENEFITS\",\"bold\":true}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Excellent metal wettability and high film strength for any moisture condition.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Minimized inventory — can be used as worm gear lubricants. Meets AGMA compounded lubricant specifications.\"}]}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"SPECIFICATIONS AND APPROVALS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"It meets the Naphthenic Uninhibited transformer oil specification IEC 60296\"}]}]','2024-08-15 07:01:46.361000','2025-05-26 11:40:29.378000','2024-08-15 07:01:50.843000',2,4,'208',NULL,NULL,NULL,'ATLANTIC TRANSFORMER OIL'),(6,'ATLANTIC TURBINE OIL','atlantic-turbine-oil','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC TURBINE OIL (GX) series is a high-performance circulating lubricant designed for applications such as steam and hydro turbine sets as well as other systems where it is needed that the lubricant has a long service life. Developed using base stocks that have undergone a significant amount of refining, as well as an additive system that offers a very high degree of chemical and thermal stability, as well as a rapid and thorough separation from water and a high level of resistance to emulsification.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC TURBINE OIL (GT) is a gas turbine oil that is designed for demanding applications due to its high performance and long life. These innovative products are made with high-quality hydro-treated base stocks, which provide exceptional thermal and oxidation resistance. Additionally, they contain additives that have been carefully selected because they are engineered to provide the deposit control and ‘keep-clean’ performance that is necessary for severe-duty gas turbines, in addition to the excellent water separability that is required for steam turbine operation. In order to fulfill the load-carrying requirements of geared turbines, the formulations also feature an antiwear system that does not contain zinc.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"APPLICATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC TURBINE OIL (GX) was developed primarily for use in turbo machine gears and regulation systems. These systems are responsible for the lubrication of gas turbines, steam turbines, combined cycle turbines, gearboxes, regulation circuits, turbochargers with separate oil circuits, and hydraulic turbines.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC TURBINE OIL (GT) was developed specifically for the lubrication (bearings, gearboxes) and control circuit of steam and gas turbines, as well as turbines that are subjected to high levels of thermal stress. These oils are also suited for the lubrication of turbine-linked gears.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PRODUCT FEATURES & BENEFITS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC Turbine Oil (GX)\",\"bold\":true}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Excellent performances in terms of oxidation resistance, antifoaming, air release, and water release.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Enhanced anti-wear and extreme pressure capacities, which make it possible to lubricate the gearboxes that are driven by the turbine.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Excellent resistance to oxidation and corrosion.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Maintaining a secure operation of the control system.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Resistant to sludging and varnishing; good air release and high resistance to foaming.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Very versatile, with a variety of possible uses.\"}]}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC Turbine Oil (GT)\",\"bold\":true}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Extended intervals between oil draining on high-temperature gas turbines.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Extremely strong resistances to oxidation and thermal instability as a result of high-performance base stocks.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Superior resistance to wear and tear.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Simplified maintenance and storage—meets the needs of key builders for the specifications of both steam and gas turbines.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Good demulsibility.\"}]}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"RECOMMENDATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC Turbine Oils (GX): ISO 6743-5 L TSA/TSE/TGA/TGB/TGE, DIN 51515 Part 1, JIS K 2213 Type 2, B S 489\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC Turbine Oils (GT): ISO 6743-5 TSA/TSE/TGA/TGB/TGE/TGSB/TGSE\"}]}]','2024-08-15 09:02:09.826000','2025-05-26 11:40:53.600000','2024-08-15 09:12:47.064000',2,4,'1,4,5,20,25,208',NULL,NULL,NULL,'ATLANTIC TURBINE OIL'),(7,'ATLANTIC TRANSFORMER – Electrical Insulating Oil','atlantic-transformer-electrical-insulating-oil-1','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC TRANSFORMER OIL is an electrical insulating oil of the highest grade that is produced from mineral oils that have been thoroughly purified. It enables excellent heat transport, in addition to having good dielectric characteristics and oxidation stability. Because of its superior performance at low temperatures, transformers, switchgears, and other types of electrical equipment are designed to make use of this material.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"It is available in two different grades:\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC TO-U is unrestricted and complies with the standards set forth by IEC 60296 (82) Class I and II, BS 148:97 Class I and II, and ASTM D 3487 type I.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC TO-I has had its oxidation resistance enhanced with the addition of an inhibitor, and it satisfies the requirements of IEC 60296 (82) Class IA and IIA.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"APPLICATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"It is suggested to use ATLANTIC TRANSFORMER OIL in oil-filled transformers and switchgear in applications in which the oil is required as an insulation medium or as a heat transfer medium. They cannot be used in oil-filled cables, in particular impregnation processes, or in capacitors since they do not meet the requirements for such uses.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC TO-U is intended for use in applications that need goods meeting the requirements of ASTM D 3487 type I, IEC 60296 (82) Class I or II, or BS 148:97 Class I or II.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Applications that require IEC 60296 (82) Class I A or II A goods are the ones in which ATLANTIC TO-I is to be utilized.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PRODUCT BENEFITS\",\"bold\":true}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"In order to lengthen the service life of applications that have a high level of exposure to air, ATLANTIC TO-U is treated with a phenolic anti-oxidant that prevents it from oxidizing.\"}]},{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Very high levels of purity help to reduce the effects of power factors and keep temperatures from rising.\"}]},{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Outstanding capacity for heat transport and fluidity, which contribute to excellent cooling capabilities.\"}]},{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"The oil that has been refined to a high degree ensures both chemical stability and service longevity.\"}]},{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Protection of equipment that is extremely effective owing to the fact that it does not corrode and has cleaning properties.\"}]},{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Increase in the service life of both the transformer and the oil.\"}]},{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Enhancements to both security and productivity.\"}]},{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Lessening the frequency and expense of breakdowns and maintenance.\"}]}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"RECOMMENDATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"IEC 60296 (82) Class I and II, BS 148:97 Class I and II, and ASTM D 3487 type I are the requirements for ATLANTIC TO-U.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"IEC 60296 (82) Class IA and IIA is referred to in ATLANTIC TO-I.\"}]}]','2024-08-15 09:52:11.129000','2025-05-26 11:43:16.800000','2024-08-15 09:52:16.941000',2,4,'1,4,5,20,25,208','Electrical Insulating Oil',NULL,NULL,'ATLANTIC TRANSFORMER '),(8,'ATLANTIC STEAM CYLINDER OIL – Gear and Cylinder','atlantic-steam-cylinder-oil-gear-and-cylinder','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC STEAM CYLINDER ENGINE OIL is an extra high performance, high viscosity oil that is designed for use in enclosed worm gears that are operating at moderate to high speeds and temperatures, as well as in settings that require high viscosity oils due to heavy loads, slow speeds, or high temperatures. They are also designed to be utilized in every conceivable application involving steam engines. They are made from high-quality base stocks that are resistant to oxidation and thermal degradation, as well as the accumulation of hazardous deposits that can be brought on by the high operating temperatures of steam cylinders. In addition to providing protection against rust and corrosion, good film strength, great lubricity, and resistance to water washout, these coatings are also water resistant.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PROPERTIES\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC STEAM CYLINDER ENGINE OIL is formulated with the appropriate additives to enhance the frictional and load-carrying properties, and it can be applied both by drip feed cups and by force-feed mechanical lubricating devices. This is because the frictional and load-carrying properties are enhanced.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PRODUCT BENEFITS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Good protection of equipment at high temperatures caused by changeable load circumstances, reducing the likelihood of unforeseen downtime and the need for equipment replacement.\"}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Superior resistance to the formation of deposits even when exposed to high temperatures.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"The outstanding stability of the film even at high temperatures.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Very high water separation quality in the absence of chemicals.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Excellent level of quality and integrated performance characteristics.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Longer oil shelf life as a result of its natural oxidation stability.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Provision of an excellent sealing coating in steam applications on round rods and in glands.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"As a result, there are fewer unscheduled stoppages and lower expenses associated with maintenance.\"}]}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"APPLICATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC STEAM CYLINDER ENGINE OIL is specially formulated to meet the requirements of the following applications:\"}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Steam Cylinder Oil 355 Compressor cylinders, couplings, and bearings, as well as the breaking-in process for new cylinders. Splash lubrication of enclosed worm gears that are working at speeds ranging from moderate to high while subjected to high temperatures.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Steam Cylinder Oil 460&680 Suggested for the uses mentioned above, albeit under harsher conditions in terms of temperature and load.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Steam Cylinder Oil 1000 It can be used as a tempering oil at bath temperatures of up to 290 degrees Celsius, and it can also be utilized in situations where quick separation from condensate is important, such as in situations in which exhaust steam is employed for process work.\"}]}]}]','2024-08-15 10:02:41.994000','2025-05-26 11:42:56.226000','2024-08-15 10:19:16.742000',2,4,'1,4,5,20,25,208',NULL,NULL,NULL,'ATLANTIC STEAM CYLINDER OIL'),(9,'ATLANTIC REFRIGERATION COMPRESSOR OIL','atlantic-refrigeration-compressor-oil','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC REFRIGERATION COMPRESSOR OIL is a high-performance lubricant that is produced from exceptionally low-wax naphthenic oil. Its primary purpose is to be utilised in refrigeration compressors, which call for unrestricted oils. It is indicated for use in refrigeration compressors that use ammonia, CFC (chlorofluorocarbon), or HCFC (hydrochlorofluorocarbon) refrigerants and have an evaporator temperature that can range down to roughly -35 degrees Fahrenheit or -37 degrees Celsius. It is compatible with the vast majority of refrigerants, with the exception of sulfur dioxide, but it is not advised for use with hydrofluorocarbon (HFC) refrigerants.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PROPERTIES\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"As a result of the fact that ATLANTIC REFRIGERATION COMPRESSOR OIL has low pour points and excellent fluidity at very low temperatures despite the fact that it contains almost no wax, the use of this oil helps to ensure that evaporator tubes are kept clean, which improves heat transfer and reduces the amount of downtime required for maintenance. They have a high degree of chemical stability and can be used for lubricating purposes in both bearings and cylinders. Because of the extremely low moisture content, it is imperative that every effort be made to ensure that they remain dry. This helps to decrease the chance of ice forming in the expansion valves, and it also helps to reduce the risk of oil degradation and copper plating.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PRODUCT BENEFITS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Low wax content; long service life, which translates to less downtime and cheaper expenses for maintenance.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Low foam, which means that it releases dissolved refrigerant without producing an excessive amount of foam.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Low FLOC point, meaning it is free of wax and other components that could potentially separate apart at lower temperatures.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Excellent chemistry and thermodynamics stability.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Improves the effectiveness of the refrigeration equipment; Shields the various parts of the cooling loop from damage.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Good heat transfer from the evaporator and flow at low temperatures for the most effective operation of the system.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Multi-purpose lubricants.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"APPLICATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC REFRIGERATION COMPRESSOR OIL is a specialized product that has been developed for the purpose of the cylinder and bearing lubrication in the vast majority of traditional refrigeration compressors as well as other types of machinery that operate at temperatures below zero. Common applications include huge industrial reciprocating and rotary refrigeration compressors, industrial applications including food freezing and cold storage plants, and maritime refrigeration applications. Other applications include automobile air conditioning and refrigeration systems. Although its primary application is with ammonia as a refrigerant, it can also be utilized with certain halocarbons.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"RECOMMENDATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Satisfies or outperforms the requirements set forth by AMMONIA, CFC, and HCFC SYSTEMS.\"}]}]','2024-08-15 10:49:42.359000','2025-05-26 11:49:32.165000','2024-08-15 11:05:37.720000',2,4,'1,4,5,20,25,208',NULL,NULL,NULL,'ATLANTIC REFRIGERATION COMPRESSOR OIL'),(10,'ATLANTIC HEAT TRANSFER FLUIDS','atlantic-heat-transfer-fluids','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC HEAT TRANSFER FLUIDS are high-performance HTOs that are designed to be used in closed indirect heating installations. These fluids are resistant to thermal cracking and chemical oxidation and are created from highly refined base stocks. As a result of these oils’ resistance to thermal cracking at the temperatures of operation for which they are approved, the flash points of these products will not experience a considerable drop during service.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PRODUCT BENEFITS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Performance and dependability, even when operating in demanding environments.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Good thermal characteristics and high resistance to thermal cracking and breakdown\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Excellent resistance to both heat and oxidation\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Enhances the overall operational effectiveness of the heat transfer system\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Helps to protect equipment by preserving its viscosity and film thickness even when subjected to high temperatures Completely free of sludge and coke deposits\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Excellent fluidity even at low temperatures.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Guaranteeing the reliability of the product by third-party verification.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PROPERTIES\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC HEAT TRANSFER FLUIDS are thermally stable and are capable of extraordinarily long service life without deposit development or a rise in viscosity. Moreover, these fluids do not require any special maintenance. They exhibit very particular temperatures and thermal conductivities, both of which contribute to a greater rate of heat dissipation. They have a high heat transfer efficiency, and their viscosities are such that they may be easily pumped at temperatures ranging from start-up to operational with no difficulty.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"APPLICATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC HEAT TRANSFER FLUIDS are recommended for use in open and closed installations where the bulk oil temperature ranges are as outlined in the table below and where the minimum shutdown temperatures are not below -7oC. This recommendation applies to situations where ATLANTIC HEAT TRANSFER FLUIDS will be subjected to temperatures that are not below -7oC.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Bulk Oil Temperature Ranges for ATLANTIC HTO 22                               Bulk Oil Temperature Ranges for ATLANTIC HTO 32\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Closed Systems: -7ᵒC to 285ᵒC                                                                     Closed Systems: -7ᵒC to 315ᵒC)\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Open Systems: -7ᵒC to 150ᵒC                                                                       Open Systems: -7ᵒC to 180ᵒC\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Open systems provided that the bulk temperatures do not exceed the maximum temperatures quoted in the table above.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Closed, cold-oil sealed, indirect heating and cooling systems in all kinds of industrial processes operating at bulk oil temperatures up to the maximum temperatures quoted in the table above and at atmospheric pressure.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"RECOMMENDATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Meets or exceeds the requirements of FDA 21 CFR 178.3570, DIN 51522 (1998 – 11), NSF H1, NSF HT1 Classified as ISO 6743-12 Family Q.\"}]}]','2024-08-15 11:16:58.262000','2025-05-26 11:38:47.497000','2024-08-15 11:17:06.567000',2,4,'1,4,5,20,25,208',NULL,NULL,NULL,'ATLANTIC HEAT TRANSFER FLUIDS'),(11,'ATLANTIC GEAR EP – Extreme Pressure Oil','atlantic-gear-ep-extreme-pressure-oil','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC GEAR EP series gear oils are exceptionally high-performance gear oils that have outstanding extreme pressure characteristics and load-carrying properties. These oils are designed for use in all different kinds of enclosed gear drives that have circulation or splash lubrication systems. It is carefully developed to suit the demands placed on seals, bearings, and gears under high levels of stress.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PRODUCT BENEFITS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Superior ability to withstand oil oxidation and heat decomposition.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Enhanced protection against bearing wear and tear.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Strong resistance to the accumulation of sludge and deposits.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Many different fields of use.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Extended service life for both the equipment and the oil.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Denting caused by produced debris and wear particles was reduced.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Improved protection against gear wear caused by micro pitting.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"The high degree of compatibility with a variety of different seal materials.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Enhanced protection against the rust and corrosion of steel, as well as the corrosion of copper and alloys of soft metals.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"The ability to resist foaming and the development of emulsions.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"APPLICATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"The ATLANTIC GEAR EP series is utilized in a vast array of industrial and maritime applications, particularly those involving spur, helical, bevel, and worm gears. The following are examples of specific applications:\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Industrial gearing for heavy-duty applications such as conveyers, agitators, dryers, extruders, fans, mixers, presses, pulpers, pumps (including oil well pumps), screens, and extruders;\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Maritime gearing, includes the ship’s primary propulsion, centrifuges, and deck machineries such as winches, windlasses, cranes, turning gears, pumps, elevators, and rudder carriers;\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Shaft couplings, screws, and heavily loaded plain and rolling contact bearings that operate at low speeds are examples of uses that do not include gears.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"SPECIFICATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"AGMA 9005-E02, DIN 51517-3: 2009-06, ISO 12925-1 Typ\"}]}]','2024-08-15 11:32:25.611000','2025-05-26 11:39:15.823000','2024-08-15 11:32:30.032000',2,4,'1,4,5,20,25,208',NULL,NULL,NULL,'ATLANTIC GEAR EP'),(12,'ATLANTIC GAS COMPRESSOR OIL – Synthetic','atlantic-gas-compressor-oil-synthetic','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC GAS COMPRESSOR OIL is a high-performance synthetic gas compressor lubricant that is based on polyethylene glycol. It is designed to reduce friction and wear in gas compressors. It is intended for use in enclosed-type gas compressors for hydrocarbon and chemical gases, in which the crankcase and bearings work in an atmosphere filled with gas. It is well suited for use in maritime applications, specifically on vessels transporting cargoes of liquefied gas.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PRODUCT BENEFITS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Decreased component wear; decreased hydrocarbon and chemical gas solubility; increased resistance to corrosion.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Service for a wide variety of gases, including butadiene and vinyl chloride, as part of a multi-gas system.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Formation of deposits with less carbon.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Protection against rust and corrosion that is effective.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Contributes to the reduction of costs associated with equipment maintenance and helps extend the life of the equipment.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"High resistance to water, and the ability to resist wash-off by moist gases, which helps to maintain the lubrication and protection of equipment.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Outstanding resistance to wear and tear.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"APPLICATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC GAS COMPRESSOR OIL is formulated to handle a variety of hydrocarbons and other gases while providing cylinder lubrication in reciprocating compressors. This oil is intended exclusively for this purpose. Applications include:\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Liquefied natural gases, such as methane and ethane, and liquefied petroleum gases, such as propane and butane, are examples of liquefied gases.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Chemical gases that are composed of hydrocarbons, including ethylene, propylene, and butylene.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Ammonia, vinyl chloride monomer, and butadiene are all examples of chemical gases.\"}]}]','2024-08-15 11:41:08.328000','2025-05-26 11:49:48.963000','2024-08-15 11:41:12.996000',2,4,'1,4,5,20,25,208',NULL,NULL,NULL,'ATLANTIC GAS COMPRESSOR OIL'),(13,'ATLANTIC CIRCULATING OILS','atlantic-circulating-oils','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC CIRCULATING OIL is a premium performance circulating lubricant that is formulated from highly refined base stocks and an additive system. This combination provides an extremely high level of chemical and thermal stability, rapid and complete separation from water, and high resistance to emulsification. Additionally, this lubricant offers excellent protection against rust and corrosion, including resistance to salt water, and good antiwear properties.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PROPERTIES\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC CIRCULATING OIL offers good air release properties that allow entrained air to separate, hence preventing pump cavitation and unpredictable performance. These benefits can be attributed to the oil’s outstanding air release capabilities. They have a high viscosity index, which assures that the film thickness will not change much with temperature and that there will be minimal power loss during the warm-up period.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PRODUCT FEATURES AND BENEFITS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Prevents cavitation, as well as noisy and irregular operation of the pump.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"A high level of resistance to sludging and varnishing, as well as excellent chemical and thermal stability.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Extended oil charge life in circulation systems and lower costs associated with oil change.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Superior resistance to foaming and exceptional ability to release air.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Protection against rust and corrosion over the long term.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Increased longevity of the device decreased the need for maintenance and downtime.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Good resistance to wear and low coefficient of friction.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Very adaptable, with a variety of possible uses.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Enhanced efficiency in operation; qualities that are very good at releasing water.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"APPLICATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC CIRCULATING OILS are formulated to work optimally in a variety of settings, including the ones listed below:\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Turbines that receive their oil through splashing, bathing, ring oiling, or some other form of mechanical lubrication.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Continuous service in plain bearings and roller bearings, as well as gearing for parallel shafts.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Circulation systems for land-based and maritime steam turbines, hydro turbines, and some gas turbines, including pumps, valves, and other ancillary equipment; circulatory systems for hydro turbines.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Compressors and vacuum pumps that are able to handle air, natural gas, and inert gases, and have discharge temperatures that do not exceed 150 degrees Celsius.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Hydraulic pumps of a rather severe nature.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"RECOMMENDATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Meets or exceeds the requirements of DIN 51515-1: 2010-02, DIN 51517-2: 2009-06, GE GEK 46506D, GE GEK 27070, GE GEK 28143A, JIS K-2213 Type 2 w/Additives (1983).\"}]}]','2024-08-15 11:55:16.195000','2024-09-02 05:14:30.953000','2024-08-15 11:55:21.056000',2,2,'1,4,5,20,25,208',NULL,NULL,NULL,'ATLANTIC CIRCULATING OILS'),(14,'ATLANTIC CHAIN OIL – High Temperature, Synthetic','atlantic-chain-oil-high-temperature-synthetic','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC CHAIN OIL is a synthetic, high-temperature chain oil that was developed specifically for the lubrication of hot chains used in conveyor systems, food processing, plastic film stretching, fiberboard manufacturing, and ceramics. This ensures that the chains continue to function reliably despite the high temperature and stress that they are subjected to.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PROPERTIES\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC CHAIN OIL is created by combining a carefully balanced blend of high-molecular-weight, synthetic hydrocarbons and a synthetic ester base fluid with a very effective additive combination consisting of anti-wear, extreme pressure additives, corrosion passivation, friction modifiers, detergents, antifoams, and oxidation inhibitors. This mixture is then mixed with a very effective additive combination. It does not include silicone. When compared to mineral oil and other standard chain lubricants, ATLANTIC CHAIN OIL has the ability to reduce the buildup of carbon and lengthen the time between cleanings.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PRODUCT FEATURES AND BENEFITS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Prolongs the useful lives of bearings, chains, and conveyors.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Thermal stability; exceptional penetrating qualities that allow for the creation of a lubricating coating in a short amount of time.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Outstanding lubricating properties, even when running at high temperatures.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Resiliency in the face of pressure and wear.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Low flammability below 250 degrees Celsius.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Excellent capacity for the regeneration of spent oil.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Outstanding viscosity at low temperatures for risk-free beginnings.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Excellent adherence with little loss of hold.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Little residue as a result of using only entirely synthetic components.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"A lower level of carbon residue; a decrease in the amount of lubricant consumed and the risk for loss.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"APPLICATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC CHAIN OIL is formulated to be used specifically for the lubrication of hot conveyor chains in drying ovens, roller chains, chains or clips with ball bearings, slide chains with the lubrication of slide rails, chain joints and bolts, dryers, steamers, and other applications requiring a high level of durability.\"}]}]','2024-08-15 12:08:33.099000','2024-09-05 11:25:56.793000','2024-08-15 12:08:37.019000',2,3,'1,4,5,20,25,208',NULL,NULL,NULL,'ATLANTIC CHAIN OIL'),(15,'ATLANTIC AIR COMPRESSOR OIL – Synthetic','atlantic-air-compressor-oil-synthetic','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC SYN Air Compressor Oil is a wholly synthetic, high-quality lubricant designed specifically for use in air compressors. It provides an extended oil drain interval and can withstand discharge temperatures of up to 200 degrees Celsius in both mobile and fixed settings. It functions very well in screw-type compressors that utilize oil injection cooling.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC D-Series Air Compressor Oil is a synthetic di-ester air compressor lubricant of superior quality. It is part of the ATLANTIC D-Series. It has been purposely created for installation in stationary equipment that operates at consistently high temperatures of up to 200 degrees Celsius, such as heavy-duty reciprocating air compressors.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC Premium Air Compressor Oil is a high-performance, ashless air compressor oil that is formulated using narrow-cut, high-quality mineral oils. It offers premium levels of performance. It is utilized in virtually all types of compressors, reciprocating, rotary screw, and rotary vane-type machines, with discharge temperatures of up to 150 degrees Celsius, in both stationary and mobile installations.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PRODUCT FEATURES AND BENEFITS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Increased safety, decreased potential for fires and explosions, longer intervals between valve cleanings, reduced deposit formation in discharge lines, extended cleaning intervals for valves, etc.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Longer oil-drain intervals\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"The reduced propensity for carbon formation, particularly on the valves.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"APPLICATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC AIR COMPRESSOR OIL is principally developed for use in rotary screw and vane air compressors. It is especially effective for continuous high-temperature operation with discharge temperatures of up to 200 degrees Celsius. It is appropriate for use in systems that have a previous record of excessive oil deterioration, poor valve performance, or deposit development. It is compatible with all of the metals that are used in the construction of compressors, as well as the elastomers that are compatible with mineral oil and are used in seals, O-rings, and gaskets, as well as compressor systems that have critical gears and bearings, units that operate under severe conditions, and reciprocating air compressor crankcases and cylinders.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC AIR COMPRESSOR OILS are not advised for use in air compressors that are utilized in applications that involve breathing air.\"}]}]','2024-08-15 12:16:19.106000','2025-05-26 11:50:12.953000','2024-08-15 12:16:21.869000',2,4,'1,4,5,20,25,208',NULL,NULL,NULL,'ATLANTIC AIR COMPRESSOR OIL'),(16,'ATLANTIC RUSTILLO PROTECTION OILS','atlantic-rustillo-protection-oils','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC RUSTILLO OILS are designed to protect parts from the risks of corrosion during storage and transportation.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"They can be applied to all ferrous metals, from cast iron to alloyed steels.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC PROTECTION OILs constitutes wide range of products in accordance with the required properties of the\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"protective film;\"}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Nature and thickness of the film: oil based, waxy or solvented.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Resistance over time: inter-operations protection with water-repellent effect or a longer-lasting protection.\"}]}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PRODUCT FEATURES & BENEFITS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"RUSTILLO 3000 is fluidized protection fluid with superior water displacement capability that effectively eliminates all traces\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"of water from metal surfaces by coating the surface with a thin film to give medium-term protection.\"}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Provides protection for six months during indoor storage and two months during outdoor, sheltered storage.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Rapid solvent evaporation.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Good resistance to humid air.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Complies with Standard ISO 6743/8 (1987) : REE category\"}]}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"RUSTILLO 5000 is a water-repellent protective fluid, leaving an oily film on parts, providing medium-term protection\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"following surface treatment. Used in temporary storage in the mechanical engineering industry.\"}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Medium protection from 8 to 10 months indoors and 3 months outdoors.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Very high water-repellent capability.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Quick drying.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Complies with Standard ISO 6743/8 (1987) : REE category.\"}]}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"RUSTILLO 9000 is corrosion protection fluid that coats parts with a soft, fatty film a slight dewatering effect. It is\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"recommended for protecting polished sheet metal, black sheet metal, galvanized sheet metal, strip steel and, generally\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"speaking, all types of metal stored outside and of course, inside for a very long time. Also suited to packaging bulky material\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"or different types of parts in readiness for maritime transport.\"}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Protection over very long periods; 2 months outside (exposed to the weather), 9 months outside, in covered area & 18 months when stored inside.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Rapid evaporation of the solvent.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Very high spreading power.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Excellent resistance to humidity, surface water and acid atmospheres.\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Complies with American Standards AXS 673, AXS 1759 grades 1and 2, as well as with MIL-C-972 grade 3\"}]}]},{\"type\":\"list\",\"format\":\"unordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Complies with Standard ISO 6743/8 (1987): category RFF.\"}]}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"APPLICATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC RUSTILLO OILS can be applied by dipping spray and brush application. They can be removed with the aid\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"of solvents and alkali cleaners.\"}]}]','2024-08-17 05:26:44.202000','2024-08-31 07:19:16.734000','2024-08-17 05:26:49.064000',2,2,'1,4,5,20,25,208',NULL,NULL,NULL,'ATLANTIC RUSTILLO PROTECTION OILS'),(17,'ATLANTIC QUENCH OILS','atlantic-quench-oils','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC QUENCHOLs are a series of premium quality oils designed specially for use as heat-treating fluids. It consists of two grades: one medium-viscosity, dual-purpose oil for both conventional quenching and marquenching; and one high-viscosity oil specifically for marquenching. Both grades are made with highly refined base oil fortified with special additives to meet the exacting requirements of heat-treating oils.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC QUENCHOL 32 is a medium-viscosity, dual-purpose oil designed for both conventional quenching and\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"marquenching. It has exceptional oxidation stability, and therefore does not require replacement as frequently as conventional oils. In addition, it will leave fewer residues on quenched parts.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC QUENCHOL 100 is a high viscosity, long-life oil designed specially for marquenching. It has the high flash point required in high temperature protected-atmosphere systems and it has the excellent oxidation stability required in unprotected systems (exposed to atmosphere). It has unusually fast quenching speeds, parts quenched in it attain the same hardness as comparable parts quenched in conventional oils at lower temperatures. Thus, it is possible to take full advantage of the minimum distortion feature of marquenching without the loss of hardness often attendant in marquenching. It is easily removed from quenched parts with only a mild alkali wash. Also, because of its high flash point, it minimizes smoking and improves working conditions\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PRODUCT FEATURES & BENEFITS\",\"bold\":true}]},{\"type\":\"list\",\"format\":\"ordered\",\"children\":[{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"High resistance to thermal and oxidation breakdown yields longer service life, less viscosity increase in service and fewer carbonaceous deposits that must be cleaned from quenched parts.\"}]},{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Exceptionally good oxidation stability, which means lower fuel costs, lower drag-out losses, and reduced clean-up costs for the user.\"}]},{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"Non-corrosive to steel and residues easily removed from quenched parts with only a mild alkali wash.\"}]},{\"type\":\"list-item\",\"children\":[{\"type\":\"text\",\"text\":\"The high flash point minimizes smoking and improves working conditions.\"}]}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"APPLICATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC QUENCHOL 32 is a dual-purpose oil designed for both conventional quenching and marquenching. It can beused in open quenching systems where operating temperatures are as high as 93ºC and in closed systems in which temperatures are as high as 150ºC.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC QUENCHOL 100 is designed specially for marquenching. It is recommended for protected-atmosphere systems where temperatures are as high as 190ºC and unprotected systems (exposed to atmosphere) with quench bath temperatures up to 135ºC.\"}]}]','2024-08-17 06:08:02.354000','2024-08-31 07:24:20.446000','2024-08-17 06:08:05.036000',2,2,'1,4,5,20,25,208',NULL,NULL,NULL,'ATLANTIC QUENCH OILS'),(19,'ATLANTIC NEAT CUTTING OILS NCO-10','atlantic-neat-cutting-oils-nco-10','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC NEAT CUTTING OILS NCO-10 is a heavy-duty, dual- or tri-purpose cutting oil with a medium viscosity. It provides outstanding cutting performance on the free-machining stock as well as tough metals, even in the most demanding machining operations. This oil is anti-mist and will not discolor non-ferrous metals. It is suggested for all types of grinding operations, including the grinding of stainless steels and high-alloy steels under extreme conditions. It decreases wheel loading, increases wheel life, enhances finishes, and reduces operating temperatures.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"APPLICATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC NEAT CUTTING OIL 10 is recommended for free-machining stocks as well as tough, sluggish metals, even when performing rigorous machining operations. It is suggested for all types of grinding operations, including the grinding of stainless steels and high-alloy steels under extreme conditions.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PRODUCT FEATURES & BENEFITS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Outstanding machining abilities\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Multifunctional applications\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Reduced misting propensity\"}]}]','2024-08-17 06:46:27.132000','2024-08-31 07:25:27.416000','2024-08-17 06:46:31.973000',2,2,'1,4,5,20,25,208','NCO-10',NULL,NULL,'ATLANTIC NEAT CUTTING OILS'),(20,'ATLANTIC SOLUBLE CUTTING SYNTHETIC FLUIDS','atlantic-soluble-cutting-synthetic-fluids','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC SYN-CUT OILs are high-performance, multi-purpose water-soluble, semi-synthetic cutting fluids. They are formulated from high quality base oils, emulsifiers and additives to provide effective machining performance in moderate and heavy duty machining operations on ferrous and non-ferrous materials. ATLANTIC SYN-CUT OILs are readily emulsifiable in water and form stable micro-emulsions. The products are engineered to be suitable for a wide range of water qualities and are resistant to foaming, even in high pressure systems. Their emulsions can provide extended coolant life relative to conventional soluble oils, with proper cutting fluid maintenance.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"PRODUCT FEATURES & BENEFITS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Excellent machining capabilities\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Easy to monitor and maintain\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Wide application range\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Excellent protection against rust and corrosion\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Suitable for a wide range of water qualities\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"Low odor\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"APPLICATIONS\",\"bold\":true}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC SYN-CUT OILs are recommended for a wide range of machining operations on metals of various harnesses.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"They can be equally used on both ferrous and non-ferrous metals.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC SYN-CUT1 is suitable for moderate to heavy duty, tough machining of high carbon and alloy steels.\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC SYN-CUT2 is suitable for heavy duty machining of steels and particularly for machining aluminum and its alloys\"}]},{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"with low staining potential.\"}]}]','2024-08-17 06:57:51.837000','2024-08-31 07:28:36.089000','2024-08-17 06:57:55.854000',2,2,'1,4,5,20,25,208','SYNTHETIC FLUIDS',NULL,NULL,'ATLANTIC SOLUBLE CUTTING'),(21,'ATLANTIC NEAT CUTTING OILS NCO-5','atlantic-neat-cutting-oils-nco-5','[{\"type\":\"paragraph\",\"children\":[{\"type\":\"text\",\"text\":\"ATLANTIC NEAT CU